import React from 'react'
import Modal from 'react-modal';
import AddNote from './Modals/AddNote';

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
    },
};
function Header() {

    let subtitle;
    const [modalIsOpen, setIsOpen] = React.useState(false);

    function openModal() {
        setIsOpen(true);
    }

    function afterOpenModal() {
        // references are now sync'd and can be accessed.
        subtitle.style.color = '#f00';
    }

    function closeModal() {
        setIsOpen(false);
    }

    return (
        <div style={{ width: '98%', backgroundColor: '#fdba74', justifyContent: 'center', alignItems: 'center', display: 'flex', flexDirection: 'row', padding: '1%' }}>
            <div style={{ width: '50%', fontWeight: 700, fontSize: 24, alignItems: 'center', justifyContent: 'center' }}>
                MarkDown App
            </div>
            <div onClick={() => openModal()} style={{ width: '25%', alignItems: 'center', alignContent: 'center', justifyContent: 'center', justifyItems: 'center' }}>
                <div style={{ width: 'max-content', height: 'max-content', fontWeight: 700, fontSize: 24, color: 'white', borderRadius: 800, padding: 10, cursor: 'pointer', backgroundColor: 'green' }}>
                    +
                </div>
            </div>
            <Modal
                isOpen={modalIsOpen}
                onAfterOpen={afterOpenModal}
                onRequestClose={closeModal}
                style={customStyles}
                contentLabel="Example Modal"
            >
                <AddNote closeModal={closeModal} />
            </Modal>
        </div>
    )
}

export default Header
