import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import convert from "react-from-dom";
import { marked } from "marked";
import { ArrowRight, Trash } from 'react-bootstrap-icons'

function NoteInfo({ noteInfo }) {

  const [newContent, setNewContent] = useState(noteInfo.Text);
  const htmlString = "<div>" + marked.parse(newContent) + "</div>";
  const htmlDescription = convert(htmlString);
  const dispatch = useDispatch();
  console.log(htmlString)
  return (
    <div style={{ width: '46%', margin: '1%', height: 'max-content', backgroundColor: '#86efac', padding: '1%', float: 'left', borderRadius: 5 }}>
      <Trash color='red' />
      <div style={{ width: '98%',height:250, padding: '1%', overflow: 'auto' }}>
        {htmlDescription}
      </div>
    </div>
  )
}

export default NoteInfo
