import React from 'react'
import store from '../store'
import { openModal } from '../store/modal'

function Header() {

    function addNoteModalOpen() {

        store.dispatch(openModal({
          name: 'add-note-modal'
        }))
    
      }

    return (
        <div className='w-full h-max flex px-16 py-2 bg-orange-300 items-center justify-center'>
            <div className='w-[50%] font-bold text-xl'>
                MarkDown App
            </div>
            <div onClick={()=>addNoteModalOpen()} className='w-[25%] flex items-center justify-center'>
                <div className='w-8 h-8 font-bold text-xl text-center text-white rounded-full bg-green-500 cursor-pointer'>
                    +
                </div>
            </div>
        </div>
    )
}

export default Header
