import React from 'react'
import store from '../store'
import { openModal } from '../store/modal'
import Modal from 'react-modal';

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};

// Make sure to bind modal to your appElement (https://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement('#yourAppElement');
function Header() {

    function addNoteModalOpen() {

        /*store.dispatch(openModal({
          name: 'add-note-modal'
        }))*/
        
      }

      let subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#f00';
  }

  function closeModal() {
    setIsOpen(false);
  }

    return (
        <div className='w-full h-max flex px-16 py-2 bg-orange-300 items-center justify-center' style={{width:'100%',backgroundColor:'#fdba74',justifyContent:'center',alignItems:'center',display:'flex',flexDirection:'row',padding:10}}>
            <div className='w-[50%] font-bold text-xl' style={{width:'50%',fontWeight:700,fontSize:24,alignItems:'center',justifyContent:'center'}}>
                MarkDown App
            </div>
            <div onClick={()=>addNoteModalOpen()} style={{width:'25%',alignItems:'center',alignContent:'center',justifyContent:'center',justifyItems:'center'}} className='w-[25%] flex items-center justify-center'>
                <div className='w-8 h-8 font-bold text-xl text-center text-white rounded-full bg-green-500 cursor-pointer' style={{	width:'max-content',height:'max-content',fontWeight:700,fontSize:24,color:'white',borderRadius:800,padding:10,cursor:'pointer',backgroundColor:'green'}}>
                    +
                </div>
            </div>
            <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Hello</h2>
        <button onClick={closeModal}>close</button>
        <div>I am a modal</div>
        <form>
          <input />
          <button>tab navigation</button>
          <button>stays</button>
          <button>inside</button>
          <button>the modal</button>
        </form>
      </Modal>
        </div>
    )
}

export default Header
