import React from 'react'
import Modal from './Modals/Modal';


function Header() {

    const [modalIsOpen, setIsOpen] = React.useState(false);

    function openModal() {
        setIsOpen(true);
    }



    return (
        <div style={{ width: '98%', backgroundColor: '#fdba74', justifyContent: 'center', alignItems: 'center', display: 'flex', flexDirection: 'row', padding: '1%' }}>
            <div style={{ width: '50%', fontWeight: 700, fontSize: 24, alignItems: 'center', justifyContent: 'center' }}>
                MarkDown App
            </div>
            <div onClick={() => openModal()} style={{ width: '25%', alignItems: 'center', alignContent: 'center', justifyContent: 'center', justifyItems: 'center' }}>
                <div style={{ width: 'max-content', height: 'max-content', fontWeight: 700, fontSize: 24, color: 'white', borderRadius: 800, padding: 10, cursor: 'pointer', backgroundColor: 'green' }}>
                    +
                </div>
            </div>
            <Modal openModal={openModal} modalIsOpen={modalIsOpen} setIsOpen={setIsOpen} />
        </div>
    )
}

export default Header
