import React, { useState } from 'react'
import { language, modalClose } from '../../utils/utils'
import { nanoid } from 'nanoid'
import moment from 'moment/moment'
import { addNote } from '../../store/note'
import store from '../../store'

function AddNote() {

    const [noteText, setNoteText] = useState("")

    const handleSubmit = async () => {

        let today = new Date();

        let todayy = today.getFullYear() + '' +
            ((today.getMonth() + 1) < 10 ? ("0" + (today.getMonth() + 1))
                : (today.getMonth() + 1)) + '' + today.getDate() + ", " +
            today.getHours() + ":" +
            ((today.getMinutes()) < 10 ? ("0" + (today.getMinutes()))
                : (today.getMinutes())) + ":" +
            today.getSeconds();

        const time = today.getFullYear() + '' +
            (today.getMonth() < 10 ? ("0" + today.getMonth()) : today.getMonth())
            + "" +
            (today.getDate() < 10 ? ("0" + today.getDate()) : today.getDate())
            + "" +
            (today.getHours() < 10 ? ("0" + today.getHours()) : today.getHours())
            + "" +
            (today.getMinutes() < 10 ? ("0" + today.getMinutes()) : today.getMinutes())
            + "" +
            (today.getSeconds() < 10 ? ("0" + today.getSeconds()) : today.getSeconds())
            + "" +
            (today.getMilliseconds() < 10 ? ("0" + today.getMilliseconds()) : today.getMilliseconds());


        const newNote = {
            id: nanoid(),
            Text: noteText,
            FullDate: todayy,
            Date: moment().format('lll'),
            Time: time,
            Month: (today.getMonth() + 1),
        }

        store.dispatch(addNote(newNote));
        setNoteText("");
    }

    return (
        <div style={{width:'max-content',height:'max-content',padding:5,display:'grid',gap:4}}>
            <textarea
                className='w-96 min-h-full h-48 max-h-48 p-2'
                style={{maxWidth:400,minWidth:400,maxHeight:200,minHeight:200,padding:5}}
                value={noteText}
                onChange={e => setNoteText(e.target.value)}
                placeholder={language.includes("tr") ? "Notunuz" : "Your Note"}
            />

            <div style={{width:'100%',display:'flex',gap:4,alignItems:'center',justifyContent:'center'}}>
                <button onClick={() => modalClose()} className='w-[50%] bg-gray-200 hover:bg-gray-300 rounded-lg p-2' style={{width:'50%',backgroundColor:'#e5e7eb',borderWidth:0,padding:5,borderRadius:10}}>
                    {language.includes("tr") ? "Kapat" : "Close"}
                </button>
                <button onClick={() => handleSubmit()} className='w-[50%] bg-blue-500 hover:bg-blue-600 text-white rounded-lg p-2'>
                    {language.includes("tr") ? "Ekle" : "Add"}
                </button>
            </div>
        </div>
    )
}

export default AddNote;
